                    animations: {
                        opacity: delayFunction(delay, animation(0, config)),
                        transform: [
                            {
                                scale: delayFunction(delay, animation(0, config)),
                            },
                            {
                                rotate: delayFunction(delay, animation('5', config)),
                            },
                        ],
                    },
                    initialValues: {
                        opacity: 1,
                        transform: [
                            {
                                scale: 1,
                            },
                            {
                                rotate: '0',
                            },
                        ],
                    },
                    callback: callback,
                };
            };
        };
    }
    static createInstance() {
        return new PinwheelOut();
    }
}
